const raw = items[0].json.output;

if (!raw) {
  return { json: { error: "No output received" } };
}

if (typeof raw === 'object' && raw !== null) {
    return {
        json: {
            output: raw
        }
    };
}

let braceCount = 0;
let startIndex = raw.indexOf('{');
let endIndex = -1;

if (startIndex === -1) {
   return { json: { error: "No JSON found" } };
}

for (let i = startIndex; i < raw.length; i++) {
    if (raw[i] === '{') {
        braceCount++;
    } else if (raw[i] === '}') {
        braceCount--;

        if (braceCount === 0) {
            endIndex = i;
            break; 
        }
    }
}

if (endIndex !== -1) {
    const cleanJsonString = raw.substring(startIndex, endIndex + 1);
    try {
        const parsedObject = JSON.parse(cleanJsonString);
        return {
            json: {
                output: parsedObject
            }
        };
    } catch (e) {
        throw new Error("Error parseando el JSON limpio: " + e.message);
    }
} else {
    throw new Error("No se pudo encontrar un objeto JSON completo en la respuesta.");
}

